    // require re-register MFA
    public static void reregisterMFA_Click(JFrame frame) {
        //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(null, noActiveUser);
        }
        // it is not, we can continue
        else {
            //change the cursor, we might be a while
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
            
            List<AuthenticationMethod> methods = null;
            List<AuthenticationMethod> cantdelete = new ArrayList<>();
            String defaultMethod = MFAExtras.getDefaultMethod();
            
            if (null == defaultMethod || defaultMethod.isEmpty()) {
                // we  cannot identify the default method, so we will loop through all methods
                // and delete them all. need to loop through the methods 3 times maximum
                methods = MFAExtras.getUserMfaMethods();
            
                for (int i = 0; i < 3; i++) {
                    for (AuthenticationMethod method : methods) {
                        if (method instanceof PasswordAuthenticationMethod || method instanceof EmailAuthenticationMethod) {
                            continue; // skip password and email methods
                        } else {
                            try {
                                MFAExtras.deleteMethod(method.getId());
                            } catch (ODataError ex) {
                                // if the method cannot be deleted, add it to the list
                                //cantdelete.add(method);
                            }
                        }
                    }
                }
            } else if ( defaultMethod.contains("voice") || defaultMethod.equalsIgnoreCase("sms")) {
                // we cannot delete the default method, so we will loop through all methods
                // and delete them all except for phone methods
                methods = MFAExtras.getUserMfaMethods();
            
                for (AuthenticationMethod method : methods) {
                    if (method instanceof PasswordAuthenticationMethod || 
                        method instanceof EmailAuthenticationMethod ||
                        method instanceof WindowsHelloForBusinessAuthenticationMethod ||
                        method instanceof TemporaryAccessPassAuthenticationMethod) {
                        continue; // skip password and email methods
                    // the default method is a phone method, but we need to figure out which one
                    // and act accordingly
                    } else if (method instanceof PhoneAuthenticationMethod) {
                        PhoneAuthenticationMethod phoneMethod = (PhoneAuthenticationMethod) method;

                        // if the default method is SMS or voiceMobile, we will delete all phone methods that are not mobile
                        if ((defaultMethod.equalsIgnoreCase("sms")  || defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceMobile))
                            && !phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneMobile)){
                            MFAExtras.deleteMethod(method.getId());
                        // if the default method is SMS or v1oiceMobile, we will store the mobile phone method    
                        } else if ((defaultMethod.equalsIgnoreCase("sms")  || defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceMobile))
                            && phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneMobile)) {
                            cantdelete.add(phoneMethod);
                        //if the default method is voiceAlternateMobile, we need to store the mobile and alternateMobile methods
                        } else if (defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceAltMobile)
                            && (phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneMobile)
                            || phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneAltMobile))) {
                            cantdelete.add(phoneMethod);
                        // if the default method is voiceAlternateMobile, we will delete all phone methods that are not alternate or mobile
                        } else if (defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceAltMobile)
                            && !phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneMobile)
                            && !phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneAltMobile)) {
                            MFAExtras.deleteMethod(method.getId());
                        // if the default method is voiceOffice, we will delete all phone methods that are not office
                        } else if (defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceOffice)
                            && !phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneOffice)) {
                            MFAExtras.deleteMethod(method.getId());
                        // if the default method is voiceOffice, we will store the office phone method
                        } else if (defaultMethod.equalsIgnoreCase(MFAExtras.defaultVoiceOffice)
                            && phoneMethod.getPhoneType().toString().equalsIgnoreCase(MFAExtras.phoneOffice)) {
                            cantdelete.add(phoneMethod);
                        }
                    // for all other methods, just delete them        
                    } else {
                        try {
                            MFAExtras.deleteMethod(method.getId());
                        } catch (ODataError ex) {
                            // if the method cannot be deleted, add it to the list
                            //cantdelete.add(method);
                        }
                    }
                }
                // loop through the methods that cannot be deleted and delete them
                for ( AuthenticationMethod method : cantdelete) {
                    MFAExtras.deleteMethod(method.getId());
                }
                
            } else if (defaultMethod.equalsIgnoreCase("push")) { 
                // we cannot delete the default method, so we will loop through all methods
                // and delete them all except for Microsoft Authenticator methods
                methods = MFAExtras.getUserMfaMethods();
            
                for (AuthenticationMethod method : methods) {
                    if (method instanceof PasswordAuthenticationMethod || 
                        method instanceof EmailAuthenticationMethod ||
                        method instanceof WindowsHelloForBusinessAuthenticationMethod ||
                        method instanceof TemporaryAccessPassAuthenticationMethod) {
                        continue; // skip password, email, WHFB and TAP methods
                    } else if (method instanceof MicrosoftAuthenticatorAuthenticationMethod) {
                        // if the default method is push, we will store the Microsoft Authenticator method
                        cantdelete.add(method);
                    } else {
                        try {
                            MFAExtras.deleteMethod(method.getId());
                        } catch (ODataError ex) {
                            // if the method cannot be deleted, add it to the list
                            //cantdelete.add(method);
                        }
                    }
                }
                // loop through the methods that cannot be deleted
                // and delete them
                for ( AuthenticationMethod method : cantdelete) {
                    MFAExtras.deleteMethod(method.getId());
                }
            } else  if (defaultMethod.equalsIgnoreCase("oath")) {
                // we cannot delete the default method, so we will loop through all methods
                // and delete them all except for OATH methods
                methods = MFAExtras.getUserMfaMethods();

                for (AuthenticationMethod method : methods) {
                    if (method instanceof PasswordAuthenticationMethod || 
                        method instanceof EmailAuthenticationMethod ||
                        method instanceof WindowsHelloForBusinessAuthenticationMethod ||
                        method instanceof TemporaryAccessPassAuthenticationMethod) {
                        continue; // skip password, email, WHFB and TAP methods
                    } else if (method instanceof SoftwareOathAuthenticationMethod ||
                               method instanceof HardwareOathAuthenticationMethod) {
                        // if the default method is oath, we will store the oath methods
                        cantdelete.add(method);
                    } else {
                        try {
                            MFAExtras.deleteMethod(method.getId());
                        } catch (ODataError ex) {
                            // if the method cannot be deleted, add it to the list
                            //cantdelete.add(method);
                        }
                    }
                }
                
                for ( AuthenticationMethod method : cantdelete) {
                    MFAExtras.deleteMethod(method.getId());
                }
            } else {
                methods = MFAExtras.getUserMfaMethods();
            
                for (int i = 0; i < 3; i++) {
                    for (AuthenticationMethod method : methods) {
                        if (method instanceof PasswordAuthenticationMethod || method instanceof EmailAuthenticationMethod) {
                            continue; // skip password and email methods
                        } else {
                            try {
                                MFAExtras.deleteMethod(method.getId());
                            } catch (ODataError ex) {
                                // if the method cannot be deleted, add it to the list
                                //cantdelete.add(method);
                            }
                        }
                    }
                }
            }
            
            //change the cursor back
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
        }
    }

    // remove a security method
    public static void removeMethod_Click(JFrame frame) {
         //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(null, noActiveUser);
        }
        // it is not, we can continue
        else {
            Boolean successful = false;
            String message = "Please enter the method ID you wish to remove";
            String title = "Remove Authenticaiton Method";

            String methodId = JOptionPane.showInputDialog(null, message, title, JOptionPane.QUESTION_MESSAGE).trim();

            frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));

            if (null != methodId && !methodId.isEmpty()){
                String messageSuccess = MFAExtras.getMethodName(MFAExtras.getAuthenticationMethod(methodId))
                         + " method has been deleted. \n";
                
                successful = MFAExtras.deleteMethod(methodId);
                
                if (successful) {
                    outputArea.append(messageSuccess);
                    //outputArea.scrollRectToVisible(outputArea.getVisibleRect());
                }
                else {
                    JOptionPane.showMessageDialog(frame, "Error deleting method. Please try again.");
                }
            }

            frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
        }
    }

    // Click on Register Fido 2 Sessions button
    public static void registerFido2Passkey_Click(JFrame frame) {

        //1. Request creationOptions for the user.
        //2. Provision the passkey credential with creationOptions.
        //3. Register the provisioned credential with Entra
        //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(frame, noActiveUser);
        }
        // it is not, we can continue
        else {
            //change the cursor, we might be a while
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
            Integer timeout = 5;
            //Get the Fido2 Passkey registration configuration
            WebauthnCredentialCreationOptions fido2Config = null;
            
            //CreationOptionsWithChallengeTimeoutInMinutesRequestBuilder fido2Config = null;
            try {
                fido2Config = graphClient.users().byUserId(activeUser.getId()).authentication()
                    .fido2Methods().creationOptionsWithChallengeTimeoutInMinutes()
                    .get(requestConfiguration -> {
                        requestConfiguration.queryParameters.challengeTimeoutInMinutes = timeout;
                    });
                //fido2Config = graphClient.users().byUserId(activeUser
            }
            catch (ODataError ex) {
                outputArea.append("Error getting Fido2 Passkey registration configuration.\n" + ex.getMessage());;
            }

            if (null != fido2Config) {
                //create the window to provision the passkey
                outputArea.append("Fido2 Passkey registration configuration found.\n");
                outputArea.append(fido2Config.getPublicKey().getChallenge());

                try {
                    TerminalFactory factory = TerminalFactory.getDefault();
                    CardTerminal terminal = factory.terminals().list().get(0); // Select the first terminal
                    Card card = terminal.connect("T=1"); // Connect to the card
                    CardChannel channel = card.getBasicChannel();

                    // Example CTAP command (e.g., authenticatorGetInfo)
                    byte[] command = new byte[]{(byte) 0x00, (byte) 0xA4, (byte) 0x04, (byte) 0x00, (byte) 0x00};
                    CommandAPDU apdu = new CommandAPDU(command);
                    ResponseAPDU response = channel.transmit(apdu);

                    outputArea.append("Response: " + bytesToHex(response.getBytes()));
                    card.disconnect(false);
                } catch (Exception e) {
                    JOptionPane.showMessageDialog(null, e.getMessage());
                }
            
            } else {
                JOptionPane.showMessageDialog(null, "No Fido2 Passkey registration configuration found. Please create one first.");
            }
            
            //set the cursor back to default
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
        }
    }
    //revoke sign-in sessions
    public static void revokeSignInSessions_click(JFrame frame) {
        //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(frame, noActiveUser);
        }
        // it is not, we can continue
        else {
            //get the confirmation from the user
            int response = JOptionPane.showConfirmDialog(frame, "Are you sure you want to revoke sign-in sessions for "
                + activeUser.getDisplayName() + "?", "Revoke Sign-in Sessions", JOptionPane.YES_NO_OPTION);
            
            if (response == JOptionPane.YES_OPTION) {
                frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
                try {
                    var reply = graphClient.users().byUserId(activeUser.getId()).revokeSignInSessions().post();

                    if (null != reply && reply.getValue()){
                        JOptionPane.showMessageDialog(frame, "Sign-in sessions revoked successfully.\n" +
                        "Please note that this will not sign out the user from any active sessions.\n");
                    } else {
                        JOptionPane.showMessageDialog(frame, "Error revoking sign-in sessions. Please try again.");
                    }
                } catch (ODataError ex) {
                    JOptionPane.showMessageDialog(frame, "Error revoking sign-in sessions.\n" + ex.getMessage());
                }
                frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
            }
        }
    }

    //create a TAP for the user
    public static void createTAP_click(JFrame frame) {
        //1. Get the TAP method policy
        //2. Modify the form to show the options based on the policy
        //3. Get the input from the user
        //4. Generate the request from the input
        //5. Submit the request
        //6. Display the result


        //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(frame, noActiveUser);
        }
        // it is not, we can continue
        else {
            //1. Get the TAP method policy
            //change the cursor, we might be a while
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));
            
            TemporaryAccessPassAuthenticationMethodConfiguration tapPolicy = null;
            try{
                tapPolicy = (TemporaryAccessPassAuthenticationMethodConfiguration)graphClient.policies()
                    .authenticationMethodsPolicy().authenticationMethodConfigurations()
                    .byAuthenticationMethodConfigurationId("TemporaryAccessPass").get();
            }
            catch (ODataError ex) {
                JOptionPane.showMessageDialog(null, "Error getting TAP method policy.\n" + ex.getMessage());
            }

            frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
            
            if (tapPolicy == null) {
                JOptionPane.showMessageDialog(frame, "No TAP method policy found.");
            }
            else if (tapPolicy.getState() == AuthenticationMethodState.Disabled) {
                JOptionPane.showMessageDialog(frame, "TAP method policy is disabled. Please enable it first.");
            }
            else if (!MFAExtras.checkGroupMembership(tapPolicy.getExcludeTargets()).equals("None")){
                String message = "The active user is included in a group that is excluded from the TAP method policy.\n"
                    + "Please remove the user from the group, there may be multiple groups.\n"
                    + "The group is: " + graphClient.groups().byGroupId(MFAExtras.checkGroupMembership(tapPolicy.getExcludeTargets()))
                        .get().getDisplayName();
                String title = "TAP Method Policy Exclusion";
                JOptionPane.showMessageDialog(frame, message, title, JOptionPane.WARNING_MESSAGE);
            }   
            else {
                //2. Modify the form to show the options based on the policy
                //3. Get the input from the user
                MFAExtras.tapData tapData = new MFAExtras.tapData();
                tapData.defaultLifetimeInMins = tapPolicy.getDefaultLifetimeInMinutes();
                tapData.minLifetimeInMins = tapPolicy.getMinimumLifetimeInMinutes();
                tapData.maxLifetimeInMins = tapPolicy.getMaximumLifetimeInMinutes();
                tapData.isUsableOnce = tapPolicy.getIsUsableOnce();

                MFAExtras.createTAPWindow(tapData);
            }
        }
    }

    public static void QRCode_click (JFrame frame) {
        String windowTitle = "QR Code Authentication Window";
        String errorTitle = "QRCode Method Policy Error";
        String nullMessage = "No QRCode method policy found.";
        String disabledMessage = "QRCode method policy is disabled. Please enable it first.";
        String groupExclusionMessage = "The active user is included in a group that is excluded from the QRCode method policy.\n"
                    + "Please remove the user from the group, there may be multiple groups.\n"
                    + "The group is: ";


        //check if the active user is null
        if (null == activeUser) {
            JOptionPane.showMessageDialog(null, noActiveUser);
        }
        // it is not, we can continue
        else {
            //change the cursor, we might be a while
            frame.setCursor(Cursor.getPredefinedCursor(Cursor.WAIT_CURSOR));

            //1. Get the QrCodePin method policy            
            

            try{
                qrPolicy = (QrCodePinAuthenticationMethodConfiguration)graphClient.policies()
                    .authenticationMethodsPolicy().authenticationMethodConfigurations()
                    .byAuthenticationMethodConfigurationId("QRCodePin").get();
            }
            catch (ODataError ex) {
                JOptionPane.showMessageDialog(null, "Error getting QRCode method policy.\n" + ex.getMessage());
            }
            
            // we either didn't get a policy or there was an exception getting the policy
            if (qrPolicy == null) {
                JOptionPane.showMessageDialog(frame, nullMessage);
            }
            // the qrcodepin policy is disabled
            else if (qrPolicy.getState() == AuthenticationMethodState.Disabled) {
                JOptionPane.showMessageDialog(frame, disabledMessage);
            }
            // the user is included in a group excluded from the policy
            else if (!MFAExtras.checkGroupMembership(qrPolicy.getExcludeTargets()).equals("None")){
                groupExclusionMessage += graphClient.groups().byGroupId(MFAExtras.checkGroupMembership(qrPolicy.getExcludeTargets()))
                        .get().getDisplayName();
                
                JOptionPane.showMessageDialog(frame, groupExclusionMessage, errorTitle, JOptionPane.WARNING_MESSAGE);
            } 
            // no issues  
            else {
                //2. Modify the form to show the options based on the policy
                //3. Get the input from the user
                QrCodePinAuthenticationMethod newCode = null;
                try {
                    newCode = graphClient.users().byUserId(activeUser.getId())
                        .authentication().qrCodePinMethod().get();
                } catch (ODataError e){
                    if(404 != e.getResponseStatusCode())
                        JOptionPane.showMessageDialog(null, e.getMessage());
                    else
                        newCode = new QrCodePinAuthenticationMethod();
                }

                if(null != newCode) {
                    qrCodeMethod = newCode;
                    JFrame qrCodeFrame = new JFrame();
                    qrCodeFrame.setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);
                    qrCodeFrame.setSize(600,250);
                    qrCodeFrame.setLocation(frame.getLocationOnScreen());
                    qrCodeFrame.setLayout(new GridLayout(1,1));
                    qrCodeFrame.setTitle(windowTitle);

                    MFAExtras.fillQrCodeWindow(qrCodeFrame);

                    qrCodeFrame.setVisible(true);
                }
                else
                    JOptionPane.showMessageDialog(null,"Error creating window.");
            }

            frame.setCursor(Cursor.getPredefinedCursor(Cursor.DEFAULT_CURSOR));
        }
    }

    private static String bytesToHex(byte[] bytes) {
        StringBuilder sb = new StringBuilder();
        for (byte b : bytes) {
            sb.append(String.format("%02X", b));
        }
        return sb.toString();
    }
